import*as THREE from"three";import"../styles/main.css";const canvas=document.querySelector(".result"),scene=new THREE.Scene,geometry=new THREE.BoxGeometry(1,1,2),material=new THREE.MeshBasicMaterial({color:"#FFA500"}),cube=new THREE.Mesh(geometry,material);scene.add(cube);const sizes={width:innerWidth,height:innerHeight},aspectRatio=sizes.width/sizes.height,camera=new THREE.OrthographicCamera(-1*aspectRatio,1*aspectRatio,1,-1,.1,100);camera.position.z=3,camera.position.x=2,camera.position.y=2,camera.lookAt(cube.position),scene.add(camera);const renderer=new THREE.WebGLRenderer({canvas});renderer.setSize(sizes.width,sizes.height);const clock=new THREE.Clock,loop=()=>{const e=clock.getElapsedTime();renderer.render(scene,camera),cube.rotation.x=e,window.requestAnimationFrame(loop)};loop();